    private void btEnviarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btEnviarActionPerformed
        String nome = txtNome.getText().trim();
        String porte = (String) txtPorte.getSelectedItem();
        String idade = (String) txtIdade.getSelectedItem();
        String raca = (String) txtRaca.getSelectedItem();
        String observacoes = txtObservacoes.getText().trim();

        if (nome.isEmpty()) {
            javax.swing.JOptionPane.showMessageDialog(this, "Informe o nome do pet!", "Erro", javax.swing.JOptionPane.ERROR_MESSAGE);
            return;
        }

        Pet novoPet = new Pet(usuarioId, nome, porte, idade, raca, observacoes);
        Persistencia.cadastrarPet(novoPet);
        javax.swing.JOptionPane.showMessageDialog(this, "Pet cadastrado com sucesso!");

        txtNome.setText("");
        txtPorte.setSelectedIndex(0);
        txtIdade.setSelectedIndex(0);
        txtRaca.setSelectedIndex(0);
        txtObservacoes.setText("");
    }//GEN-LAST:event_btEnviarActionPerformed


